function potencia(e,n){let t=1,a=0;for(;a<n;)t*=e,a++;return t}const limpiar=()=>{document.getElementById("base").value="",document.getElementById("exponente").value="",document.getElementById("resultado").innerHTML=""},calcular=()=>{let e=0;base=parseInt(document.getElementById("base").value),exponente=parseInt(document.getElementById("exponente").value),e=potencia(base,exponente),document.getElementById("resultado").innerHTML="La potencia de "+base+" elevado a "+exponente+" es igual a "+e};var base,exponente;