function potencia(e,n){let t,a;for(t=1,a=0;a<n;)t*=e,a++;return t}const limpiar=()=>{document.getElementById("base").value="",document.getElementById("exponente").value="",document.getElementById("resultado").innerHTML=""},calcular=()=>{base=parseInt(document.getElementById("base").value),exponente=parseInt(document.getElementById("exponente").value),calculo=potencia(base,exponente),document.getElementById("resultado").innerHTML="La potencia de "+base+"elevado a "+exponente+"es igual a "+calculo};var base,exponente;